con:config.script:
//		The Utilities project is used to make the SOAP UI projects more manageable 
//		from Git perspective.
//		
//		A SOAPUI project is stored as a single XML file, making it difficult
//		to work with multiple people on the same project simultaneously.
//		It is hard to resolve merge conflicts and they occur frequently.
//		
//		The trick used, is that we can import and export individual test cases
//		into subfolders of the project.
//		Those individual testcases can then be versioned and maintained through
//		a source control mechanism such as Git.
//		
//		First to do, is import test cases from existing folders.
//		After changing and updating test cases, you can export them back into the folders.
//		Notice that test case name comes back in folder name, so
//		a change of testcase name will result in a NEW folder, and not rename existing folder.
//		
//		After exporting, make sure to close the test cases, becuase otherwise the
//		xml of the project will still be a big one with everything inside, again
//		causing merge conflicts.
//

import org.w3c.dom.Document
import org.w3c.dom.NodeList
import org.w3c.dom.Node

import javax.xml.namespace.NamespaceContext
import javax.xml.parsers.DocumentBuilder
import javax.xml.parsers.DocumentBuilderFactory
import javax.xml.transform.Transformer
import javax.xml.transform.TransformerFactory
import javax.xml.transform.dom.DOMSource
import javax.xml.transform.stream.StreamResult
import javax.xml.xpath.XPath
import javax.xml.xpath.XPathConstants
import javax.xml.xpath.XPathFactory

import com.eviware.soapui.support.UISupport

Document loadFile(File file) {
    DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance()
    factory.setNamespaceAware(true);
    DocumentBuilder builder = factory.newDocumentBuilder()
    builder.parse(file)
}

def save(File file, Document doc) {
    TransformerFactory transformerFactory = TransformerFactory.newInstance()
    Transformer transformer = transformerFactory.newTransformer()
    DOMSource source = new DOMSource(doc)
    StreamResult streamResult = new StreamResult(file)
    transformer.transform(source, streamResult)
}

def removeTestCases(Document doc) {
    XPath xPath = getXPath()

    NodeList nodeList = (NodeList) xPath.compile("//con:testSuite").evaluate(doc, XPathConstants.NODESET)
    for (int i = 0; null != nodeList && i < nodeList.getLength(); i++) {
        Node testSuiteNode = nodeList.item(i)
        def name = testSuiteNode.attributes.getNamedItem('name').getTextContent()
        if (!name.startsWith('_')) {
	        Node childNode = testSuiteNode.getFirstChild()
	        while (childNode > null) {
	            nextSibling = childNode.nextSibling
	            if (childNode.getLocalName() == 'testCase') {
	                childNode.getParentNode().removeChild(childNode)
	            }
	            childNode = nextSibling
	        }
        }
    }
}

def addTestCases(File testSuiteFolder, Document doc) {
    String folderName = testSuiteFolder.getName()
    int index = folderName.indexOf('#testsuite#')
    if (index > 0) {
        String testSuiteName = folderName.substring(0, index)
        XPath xPath = getXPath()
        Node testSuiteNode = (Node) xPath.compile('//con:testSuite[@name=\'' + testSuiteName + '\']').evaluate(doc, XPathConstants.NODE)
        if (testSuiteNode) {
            testSuiteFolder.eachFileRecurse { f ->
                if (f.name.endsWith("#testcase#.xml")) {

                    Document testCaseDocument = loadFile(f)
                    Node n = testCaseDocument.documentElement
                    Node testCaseNode = doc.importNode(n, true)

                    testSuiteNode.appendChild(testCaseNode)
                }
            }
        } else {
            log.error("Testsuite ${testSuiteName} not found in project")
        }
    }
}

def XPath getXPath() {
    XPath xPath = XPathFactory.newInstance().newXPath()
    NamespaceContext ctx = new NamespaceContext() {
        public String getNamespaceURI(String prefix) {
            return prefix.equals("con") ? 'http://eviware.com/soapui/config' : null;
        }

        public Iterator getPrefixes(String val) {
            return null;
        }

        public String getPrefix(String uri) {
            return null;
        }
    }

    xPath.setNamespaceContext(ctx)
    xPath
}

def importTestCases(File projectFile) {
    Document doc = loadFile(projectFile)
    removeTestCases(doc)

    File projectFolder = projectFile.getParentFile()
    projectFolder.eachFileRecurse { f ->
        if (f.name.endsWith("#testsuite#")) {
            log.info("Import testsuite ${f.name}")
            addTestCases(f, doc)
        }
    }
    save(projectFile, doc)
}

def project = testRunner.testCase.testSuite.project
File projectFile = new File(project.getPath())
importTestCases(projectFile)
project.workspace.reloadProject(project)

import com.eviware.soapui.model.testsuite.TestSuite
import com.eviware.soapui.model.testsuite.TestCase
import com.eviware.soapui.model.testsuite.TestStep
import com.eviware.soapui.support.UISupport
import org.apache.xmlbeans.XmlException
import org.apache.xmlbeans.XmlOptions
import com.eviware.soapui.model.project.Project
import com.eviware.soapui.model.testsuite.TestProperty
import com.eviware.soapui.impl.wsdl.teststeps.WsdlPropertiesTestStep
import com.eviware.soapui.impl.wsdl.teststeps.WsdlRunTestCaseTestStep
import com.eviware.soapui.support.types.StringList

def export(Project project) {
    File projectFile = new File(project.getPath())
    log.info(projectFile)
    File exportFolder = projectFile.getParentFile()
    List<TestSuite> testSuites = project.getTestSuiteList()
    for (TestSuite testSuite in testSuites) {
    	   if (!testSuite.name.startsWith('_')) {
	        String testSuiteName = "${testSuite.name}#testsuite#"
	        log.info("Export testcases in TestSuite ${testSuiteName} to folder ${exportFolder}")
	        File testSuiteFolder = new File(exportFolder, testSuiteName)
	        if (!testSuiteFolder.exists()) {
	            testSuiteFolder.mkdir();
	        }
	        exportTestCases(testSuite, testSuiteFolder)
    	   }
    }
}

def clearProperties(TestCase testCase) {
    resetProperties(testCase)
    clearPropertiesValues(testCase, 'local')
    removeProperties(testCase, 'cache')
    clearRetPropertyValues(testCase)
}

def resetProperties(TestCase testCase) {
    List<TestProperty> properties = testCase.getPropertyList()
    for (TestProperty prop in properties) {
        prop.setValue('')
    }
}

def clearPropertiesValues(TestCase testCase, String testStepName) {
    WsdlPropertiesTestStep testStep = testCase.getTestStepByName(testStepName)
    if (testStep) {
        testStep.clearPropertyValues()
    }
}

def clearRetPropertyValues(TestCase testCase) {
    List<TestStep> testSteps = testCase.getTestStepList()
    for (TestStep testStep in testSteps) {
        if (testStep instanceof WsdlRunTestCaseTestStep) {
            WsdlRunTestCaseTestStep wsdlRunTestCaseTestStep = testStep as WsdlRunTestCaseTestStep
            StringList returnProperties = wsdlRunTestCaseTestStep.getReturnProperties()
            for (String returnProperty in returnProperties) {
            	def value = testStep.getPropertyValue(returnProperty)
            	if (value) {
            		testStep.setPropertyValue(returnProperty, '')
            	}
            }
        }
    }
}

def removeProperties(TestCase testCase, String testStepName) {
    WsdlPropertiesTestStep testStep = testCase.getTestStepByName(testStepName)
    if (testStep) {
        String[] propertyNames = testStep.getPropertyNames()
        for (String propertyName : propertyNames) {
            testStep.removeProperty(propertyName)
        }
    }
}

String fixLineEndings(String xml) {
    return xml.replaceAll("\\r\\n", "\n")
}

/**
 * Export all TestCases of a <code>TestSuite</code>.
 *
 * @param testSuite the TestSuite for which the testcases must be exported
 * @param testSuiteFolder the folder to save the exported testcases
 */
def exportTestCases(TestSuite testSuite, File testSuiteFolder) {
    List<TestCase> testCases = testSuite.getTestCaseList()
    for (TestCase testCase in testCases) {
        String fileName = "${testCase.name}#testcase#.xml"
        File testCaseFile = new File(testSuiteFolder, fileName)

        clearProperties(testCase)

        String xml = testCase.getConfig().newCursor().xmlText(new XmlOptions().setSavePrettyPrint())

        String sTestCaseXml = fixLineEndings(xml)
        testCaseFile.write(sTestCaseXml)
    }
}

def project = testRunner.testCase.testSuite.project
export(project)
UISupport.showInfoMessage("Export finished.")

import com.eviware.soapui.model.testsuite.TestSuite
import com.eviware.soapui.model.testsuite.TestCase
import com.eviware.soapui.support.UISupport
import org.apache.xmlbeans.XmlException
import org.apache.xmlbeans.XmlOptions
import com.eviware.soapui.model.project.Project

def removeTestCases(Project project) {

    List<TestSuite> testSuites = project.getTestSuiteList()
    for (TestSuite testSuite in testSuites) {
    	    if (!testSuite.name.startsWith('_')) {
		    List<TestCase> testCases = testSuite.getTestCaseList()
		    for (TestCase testCase in testCases) {
				testSuite.removeTestCase(testCase)
		    }
    	    }
    }
}

